import React, { useState, useEffect } from 'react'
import './About.scss'
import { motion } from 'framer-motion'

const abouts = [
    { title: 'Web Development', description: 'I am a good web developer', imgUrl: ''},
    { title: 'Audiovisual Production', description: 'I am a good audio visual producer', imgUrl: ''},
    { title: 'Marketing', description: 'I am a good marketer', imgUrl: ''}
]

const About = () => {
    return (
        <>
            <h2 className='head-text'>
                I know that
                <span> Good Design </span>
                <br />
                means
                <span> Good Business</span>
            </h2>
            <div className='app__profiles'>
                { abouts.map((about, index) => (
                <motion.div
                    whileInView={{ opacity: 1 }}
                    whileHover={{ scale: 1.1 }}
                    transition={{ duration: 0.5, type: 'tween' }}
                    className='app__profile-item'
                    key={about.title + index}
                >
                    <img src={about.imgUrl} alt={about.title} />
                    <h2 className='bold-text' style={{ marginTop: 20 }}>{about.title}</h2>
                    <p className='p-text' style={{ marginTop: 10 }}>{about.description}</p>
                </motion.div>
                ))}
            </div>
        </>
    )
}

export default About
